import React, { Fragment, lazy, Suspense } from 'react';
import { Routes, Route } from "react-router-dom";
import Layout from './Layout';

import {
  ThemeProvider,
  createTheme,
  responsiveFontSizes,
} from '@mui/material/styles';
import { deepmerge } from '@mui/utils';
import useMediaQuery from '@mui/material/useMediaQuery';
import { getDesignTokens, getThemedComponents } from 'theme/Theme';
import { ColorModeContext } from 'config/color-context';


const RequireAuth = lazy(() => import('./Containers/RequireAuth'));
const Dashboard = lazy(() => import('./Containers/Dashboard'));
const Login = lazy(() => import('./Containers/Login/index'));
const Page404 = lazy(() => import('./Containers/Page404'));

const App = () => {
  const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');
  const [mode, setMode] = React.useState();

  React.useEffect(() => {
    setMode(prefersDarkMode ? 'dark' : 'light');
  }, [prefersDarkMode]);

  const colorMode = React.useMemo(
    () => ({
      toggleColorMode: () => {
        setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
      },
    }),
    []
  );

  let theme = React.useMemo(
    () =>
      createTheme(deepmerge(getDesignTokens(mode), getThemedComponents(mode))),
    [mode]
  );

  theme = responsiveFontSizes(theme);

  return (
    <ColorModeContext.Provider value={colorMode}>
      <ThemeProvider theme={theme}>

    <Fragment>
      <Routes>
        <Route element={<Layout />}>
          <Route
            path="/"
            element={
              <Suspense fallback={'loading route dashboard'}>
                <RequireAuth>
                  <Dashboard />
                </RequireAuth>
              </Suspense>
            }
          />
          <Route
            path="/login"
            element={
              <Suspense fallback={'loading route login'}>
                <Login />
              </Suspense>
            }
          />
          <Route
            path="*"
            element={
              <RequireAuth>
                <React.Suspense fallback={'loading route Page404'}>
                  <Page404 />
                </React.Suspense>
              </RequireAuth>
            }
          />
        </Route>
      </Routes>
    </Fragment>


      </ThemeProvider>
    </ColorModeContext.Provider>
  )
}

export default App;
